import Vue from 'vue';

export declare class Tag extends Vue {
  /**
   * 标签的类型可选值为dot、border、point或不填
   */
  type?: 'dot' | 'border' | 'point'
  /**
   * 标签的样式
   * @default 'default'
   */
  color?: 'error' | 'success' | 'primary' | 'warning' | 'default'
  /**
   * 标签文字，point时不生效
   * @default ''
   */
  text?: string;
  /**
   * 当前标签的名称，使用 v-for，并支持关闭时，会比较有用
   */
  name?: string | number;
  /**
   * 标签大小,默认为正常大小
   * @default false
   */
  small?: boolean;
  /**
   * 反色
   * @default false
   */
  ghost?: boolean;
  /**
   * 是否为圆角
   * @default false
   */
  circle?: boolean;
  /**
   * 标签是否可以关闭
   * @default false
   */
  closable?: boolean;
  /**
   * 限制字长
   * @default 12
   */
  'limit-num'?: number;
  /**
   * 关闭时触发
   */
  $emit(eventName: 'on-close', event?: object, name?: string): this;
  /**
   * 选中时触发
   */
  $emit(eventName: 'on-change', checked?: boolean, name?: string): this;
}